import React, { useState } from 'react';
import './Calculator.css';
import axios from 'axios';


function Calculator() {
  const [input, setInput] = useState('');
  const [copied, setCopied] = useState(false); // ðŸ‘ˆ for the copied message

  const handleClick = (value) => {
    setInput((prev) => prev + value);
  };

  const handleEqual = async () => {
    try {
      const evaluated = eval(input).toString();
      setInput(evaluated);
  
      // Save to backend
      await axios.post('http://localhost:5000/api/history', {
        expression: input,
        result: evaluated,
      });
  
    } catch (error) {
      setInput('Error');
    }
  };
  

  const handleCopy = () => {
    if (input) {
      navigator.clipboard.writeText(input);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000); // Message disappears after 2s
    }
  };

  return (
    <div className="calculator">
      <div className="display">
        {input || '0'}
        <button className="copy-btn" onClick={handleCopy}>ðŸ“‹</button>
        {copied && <span className="copied-msg">Copied!</span>}
      </div>
      <div className="buttons">
        <button onClick={handleClear}>C</button>
        <button onClick={() => handleClick('/')}>Ã·</button>
        <button onClick={() => handleClick('*')}>Ã—</button>
        <button onClick={() => handleClick('-')}>âˆ’</button>
        <button onClick={() => handleClick('7')}>7</button>
        <button onClick={() => handleClick('8')}>8</button>
        <button onClick={() => handleClick('9')}>9</button>
        <button onClick={() => handleClick('+')}>+</button>
        <button onClick={() => handleClick('4')}>4</button>
        <button onClick={() => handleClick('5')}>5</button>
        <button onClick={() => handleClick('6')}>6</button>
        <button onClick={handleEqual} className="equal">=</button>
        <button onClick={() => handleClick('1')}>1</button>
        <button onClick={() => handleClick('2')}>2</button>
        <button onClick={() => handleClick('3')}>3</button>
        <button onClick={() => handleClick('0')} className="zero">0</button>
        <button onClick={() => handleClick('.')}>.</button>
      </div>
    </div>
  );
}

export default Calculator;
