// src/components/SignupForm.js
import React, { useState } from 'react';
import axios from 'axios';
import '../Calculator.css'; // make sure this is the correct path

const SignupForm = ({ onAuth }) => {
  const [form, setForm] = useState({ username: '', email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/users/signup', form);
      localStorage.setItem('token', res.data.token);
      onAuth(res.data.user);
    } catch (err) {
      setMessage('Signup failed');
    }
  };

  return (
    <div className="auth-container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <div className="input-wrapper">
          <input className="auth-input" type="text" name="username" placeholder="Username" value={form.username} onChange={handleChange} required />
        </div>
        <div className="input-wrapper">
          <input className="auth-input" type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} required />
        </div>
        <div className="input-wrapper">
          <input className="auth-input" type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} required />
        </div>
        <button type="submit" className="equals">Register</button>
        {message && <p>{message}</p>}
      </form>
    </div>
  );
};

export default SignupForm;
